mapper.select:
select 

from file
where fileOriginalName = #{fileOriginalName,jdbcType=VARCHAR}
      AND fileOwner = #{fileOwner,jdbcType=VARCHAR}
ORDER BY filetime DESC

mapper.update:
update file set fileStatus = #{fileStatus,jdbcType=VARCHAR}
where fileOriginalName = #{fileOriginalName,jdbcType=VARCHAR}
      AND fileOwner = #{fileOwner,jdbcType=VARCHAR}
